package ai.extend.wrapper.errors;

/**
 * Exception thrown when polling exceeds the maximum wait time.
 * 
 * <p>This error is thrown by the polling utilities when a run doesn't reach
 * a terminal state within the configured {@code maxWaitMs} duration.</p>
 * 
 * @see ai.extend.wrapper.utilities.polling.Polling#pollUntilDone
 */
public class PollingTimeoutError extends RuntimeException {
    private final int elapsedMs;
    private final int maxWaitMs;

    /**
     * Creates a new PollingTimeoutError.
     *
     * @param message   Descriptive error message
     * @param elapsedMs Actual elapsed time in milliseconds before timeout
     * @param maxWaitMs Configured maximum wait time in milliseconds
     */
    public PollingTimeoutError(String message, int elapsedMs, int maxWaitMs) {
        super(message);
        this.elapsedMs = elapsedMs;
        this.maxWaitMs = maxWaitMs;
    }

    /**
     * Returns the actual elapsed time before the timeout occurred.
     *
     * @return Elapsed time in milliseconds
     */
    public int getElapsedMs() {
        return elapsedMs;
    }

    /**
     * Returns the configured maximum wait time that was exceeded.
     *
     * @return Maximum wait time in milliseconds
     */
    public int getMaxWaitMs() {
        return maxWaitMs;
    }
}
