package ai.extend.wrapper.resources;

import ai.extend.core.ClientOptions;
import ai.extend.core.RequestOptions;
import ai.extend.resources.editruns.requests.EditRunsCreateRequest;
import ai.extend.types.EditRun;
import ai.extend.types.EditRunStatus;
import ai.extend.wrapper.utilities.polling.Polling;
import ai.extend.wrapper.utilities.polling.PollingOptions;
import java.util.HashSet;
import java.util.Set;

/**
 * Extended EditRuns client with polling functionality.
 *
 * <p>Extends the generated {@link ai.extend.resources.editruns.EditRunsClient},
 * so all standard methods (create, retrieve, delete) are inherited.
 * Adds {@code createAndPoll} for convenience.</p>
 */
public class EditRunsClient extends ai.extend.resources.editruns.EditRunsClient {

    private static final Set<EditRunStatus> NON_TERMINAL_STATUSES;

    static {
        NON_TERMINAL_STATUSES = new HashSet<EditRunStatus>();
        NON_TERMINAL_STATUSES.add(EditRunStatus.PROCESSING);
        NON_TERMINAL_STATUSES.add(EditRunStatus.valueOf("PENDING"));
        NON_TERMINAL_STATUSES.add(EditRunStatus.valueOf("CANCELLING"));
    }

    public EditRunsClient(ClientOptions clientOptions) {
        super(clientOptions);
    }

    /**
     * Creates an edit run and polls until it reaches a terminal state.
     *
     * <p>Terminal states: PROCESSED, FAILED</p>
     */
    public EditRun createAndPoll(EditRunsCreateRequest request) {
        return createAndPoll(request, PollingOptions.defaults());
    }

    /**
     * Creates an edit run and polls until it reaches a terminal state.
     *
     * <p>Terminal states: PROCESSED, FAILED</p>
     */
    public EditRun createAndPoll(EditRunsCreateRequest request, PollingOptions options) {

        RequestOptions requestOptions = options.getRequestOptions();

        EditRun createResponse = this.create(request, requestOptions);
        final String runId = createResponse.getId();

        return Polling.pollUntilDone(
                () -> this.retrieve(runId, requestOptions),
                response -> isTerminalStatus(response.getStatus()),
                options);
    }

    private boolean isTerminalStatus(EditRunStatus status) {
        return !NON_TERMINAL_STATUSES.contains(status);
    }
}
